import React, { useState, useEffect, useContext } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import NavBar1 from '../NavBar/Navabar1/NavBar1';
import UserContext from '../../Session/Session';
import { url } from '../../URL/url';

const Sellequip = () => {
  const { value } = useContext(UserContext);
  const [companyId, setCompanyId] = useState('');
  const [userId, setUserId] = useState('');
  const [modelId, setModelId] = useState('');
  const [price, setPrice] = useState('');
  const [date, setDate] = useState('');
  const [city, setCity] = useState('');
  const [companies, setCompanies] = useState([]);
  const [models, setModels] = useState([]);
  const history = useHistory();

  useEffect(() => {
    loadCompanies();
  }, []);

  const loadCompanies = async () => {
    try {
      const result = await axios.get(url + '/getcompanies');
      const data = result.data.data;
      if (!data) {
        console.error('Error: API response does not contain data');
        return;
      }
      setCompanies(data);
      if (value && value.data && value.data.userId) {
        setUserId(value.data.userId);
        console.log('User ID:', value.data.userId);
      } else {
        console.error('Error: User data is undefined or does not contain userId', value);
      }
    } catch (error) {
      console.error('Error loading companies:', error);
    }
  };

  const loadModels = async (e) => {
    const cid = e.target.value;
    setCompanyId(cid);
    try {
      const result = await axios.get(url + '/getmodels');
      setModels(result.data.data);
    } catch (error) {
      console.error('Error loading models:', error);
    }
  };

  const sellEquip = () => {
    if (price.length === 0 || date.length === 0 || city.length === 0) {
      alert('Please fill in all required fields');
    } else {
      const data = new FormData();
      data.append('companyId', companyId);
      data.append('userId', userId);
      data.append('modelId', modelId);
      data.append('price', price);
      data.append('date', date);
      data.append('city', city);

      axios
        .post(url + '/postcar', data)
        .then((response) => {
          const result = response.data;
          if (result.status === 'success') {
            alert('Equipment added successfully');
            history.push('/CustHome');
          } else {
            alert('Error: ' + result.message);
          }
        })
        .catch((error) => {
          console.error('Error submitting form:', error);
          alert('Error: Failed to submit data. Please try again.');
        });
    }
  };

  return (
    <>
      <NavBar1 />
      <div className="bg-colo3">
        <h1>Sell Equipment Page</h1>
        <div>
          <div className="col-md-3">
            <label>Select Companies</label>
            <select onChange={loadModels} className="form-control">
              {companies.map((company, index) => (
                <option key={index} value={company.id}>
                  {company.name}
                </option>
              ))}
            </select>
          </div>
          <div className="col-md-3">
            <label>Select Models</label>
            <select
              onChange={(event) => {
                setModelId(event.target.value);
              }}
              className="form-control"
            >
              {models.map((model, index) => (
                <option key={index} value={model.modelId}>
                  {model.modelname}
                </option>
              ))}
            </select>
          </div>
          <div className="col-md-3">
            <label>Price</label>
            <input
              onChange={(event) => {
                setPrice(event.target.value);
              }}
              placeholder="Enter Price"
              className="form-control input-maargin"
              type="number"
            />
          </div>
          <div className="col-md-3">
            <label>City</label>
            <input
              onChange={(event) => {
                setCity(event.target.value);
              }}
              placeholder="Enter City"
              className="form-control input-maargin"
              type="text"
            />
          </div>
          <div className="col-md-3">
            <label>Date</label>
            <input
              onChange={(event) => {
                setDate(event.target.value);
              }}
              className="form-control input-maargin"
              type="Date"
            />
          </div>
          <div className="col-md-3">
            <button onClick={sellEquip} className="btn btn-success input-maargin">
              Submit Equipment
            </button>
          </div>
        </div>
      </div>
    </>
  );
};

export default Sellequip;
