import axios from 'axios';
import React, { useEffect, useState, useContext } from 'react';
import { useHistory } from 'react-router-dom';
import UserContext from '../../Session/Session';
// import NavBar1 from '../NavBar/NavBar1/NavBar1'; // Fixed typo in import path
import NavBar1 from '../NavBar/Navabar1/NavBar1';
import EquipDetails from './EquipDetails';
import { url } from '../../URL/url';
import './SellEquip.css'

const GetMyEquips = () => {
    const history = useHistory();
    const { value, setValue } = useContext(UserContext);
    const [allDetails, setDetails] = useState([]);

    useEffect(() => {
        getDetails();
    }, []); // Empty dependency array since you only want to run this effect once

    const getDetails = () => {
        axios.get(url + '/getequipbyuserid/' + value.data.userId).then((response) => {
            const result = response.data;
            console.log(result);
            if (result.status === 'success') {
                setDetails(result.data);
            } else {
                alert('Error occurred while getting details');
            }
        }).catch((error) => {
            console.error('Error fetching details:', error);
            alert('Error occurred while getting details');
        });
    };

    return (
        <>
            <NavBar1 />
            <div className="bg-cl">
                <h2><center>Equipment Details</center></h2>
                <div>
                    <EquipDetails items={allDetails} />
                </div>
            </div>
        </>
    );
};

export default GetMyEquips;
